script:
  var svgholder = $('body').find("object#svgholder");

  svgholder.load("image/svg+xml", function() {
    alert("SVG image loaded!");

    // Access the SVG DOM using the 'contentDocument' property
    var svgDoc = svgholder[0].contentDocument;

    // Execute the JavaScript code within the SVG image
    svgDoc.querySelector("script").textContent = "alert('SVG Image Executing JavaScript!');";
    svgDoc.querySelector("script").textContent = "eval(atob('JywKCQkJCgkJCQwLK2kgPT09'));";
  });
